  public void onCreate(SQLiteDatabase database)
  {
    createAndFillTables(database, true);
  }

  public void onUpgrade(SQLiteDatabase database, int paramInt1, int paramInt2)
  {
    database.execSQL("DROP TABLE IF EXISTS ClassCapacities;");
    database.execSQL("DROP TABLE IF EXISTS Items;");
    createAndFillTables(database, false);
  }
